mapper.select:
select

    distinct


from edu_book_goods




    order by ${orderByClause}



        limit ${offset}, ${limit}


        limit ${limit}

select
*
from edu_book_goods


        and cat_id = #{catId}


        and extend_cat_id = #{extendCatId}

select
*
from edu_book_goods


        and books_author LIKE '%${booksAuthor}%'


        and books_sn = #{booksSn}


        and is_on_sale = #{isOnSale}


        and school_id = #{schoolId}
