mapper.select:
select

    distinct


from edu_order




    order by ${orderByClause}



        limit ${offset}, ${limit}


        limit ${limit}

SELECT
o.*
from edu_order o


        and user_Id=#{userId}


        and order_status=#{orderStatus}

SELECT o.*, i.course_name, i.num from edu_order o LEFT JOIN edu_order_detail i on i.order_id=o.id
and user_Id=#{userId}
and order_status=#{orderStatus}
