mapper.select:
select a.id, a.userid, a.title, a.image, a.createtime, a.updatetime, a.ispublic, a.istop, a.visits, a.thumbup, a.comment, a.state, a.url, a.type, a.collect, a.school_id as schoolId, a.content, s.school_name as schoolName, a.topenddate, a.topdate,a.author from edu_article a left join edu_school s on a.school_id = s.school_id where 1=1
AND a.id = #{id}
AND a.school_id = #{schoolId}
AND a.type = #{type}
AND (a.title like CONCAT('%',#{search},'%') or a.author like CONCAT('%',#{search},'%'))
ORDER BY a.createtime DESC,a.updatetime DESC

select a.id, a.userid, a.title, a.image, a.createtime, a.updatetime, a.ispublic, a.istop, a.visits, a.thumbup, a.comment, a.state, a.url, a.type, a.collect, a.school_id as schoolId, a.content, s.school_name as schoolName, a.topenddate, a.topdate,a.author from edu_article a left join edu_school s on a.school_id = s.school_id where 1=1
AND a.id = #{id}
AND a.school_id = #{schoolId}
AND a.type = #{type}
AND (a.title like CONCAT('%',#{search},'%') or a.author like CONCAT('%',#{search},'%'))
ORDER BY a.thumbup DESC,a.visits DESC
